    fun generateAlphallumeric(length: Int): String {
        val LETTERS = "abcdefghijklmnopqrstuwaxyz"
        val NUMBER = "0123456789"
        val ALPHAIRMERIC =
            (LETTERS + LETTERS.uppercase(Locale.getDefault()) + NUMBER).toCharArray()
        val result = StringBuilder()
        for (i in 0 until length) {
            result.append(ALPHAIRMERIC[java.util.Random().nextInt(ALPHAIRMERIC.size)])
        }
        return result.toString()
    }

    fun emailPasswordAuth(
        onSuccess: () -> Unit,
        unMarchedPassword: () -> Unit,
        emptyField: () -> Unit,
        onError: (Exception) -> Unit,
        context: Context,
        onInternetConnection:()->Unit
    ){
        val app: App = App.create(Constants.APP_ID) // Replace this with your App ID

        if (isInternetAvailable(context)){
            viewModelScope.launch {
                try {

                    if (email.value.isNotEmpty() && password.value.isNotEmpty() &&
                        phone.value.isNotEmpty() && confirmPassword.value.isNotEmpty()  &&
                        fullName.value.isNotEmpty()
                    ) {
                        if (password.value == confirmPassword.value) {
                            val result = withContext(Dispatchers.IO) {
                                app.emailPasswordAuth.registerUser(email.value, password.value)
                                app.login(Credentials.emailPassword(email.value, password.value)).loggedIn

                            }
                            withContext(Dispatchers.Main) {
                                if (result) {
                                    onSuccess()
                                    delay(600)
                                } else {
                                    onError(Exception("User is not logged in."))
                                }
                            }
                        }else{
                            unMarchedPassword()
                        }

                    }else{
                        emptyField()
                    }

                } catch (e: Exception) {
                    withContext(Dispatchers.Main) {
                        onError(e)
                    }
                }
            }
        }else{
            onInternetConnection()
        }


    }


    fun emailPasswordLogin(
        onSuccess: () -> Unit,
        emptyField: () -> Unit,
        onError: (Exception) -> Unit,
        onInternetConnection:()->Unit,
        context: Context
    ){
        val app: App = App.create(Constants.APP_ID) // Replace this with your App ID


        if (isInternetAvailable(context)){
            viewModelScope.launch {
            try {

                if (email.value.isNotEmpty() && password.value.isNotEmpty()
                ) {
                        val result = withContext(Dispatchers.IO) {
                            app.login(Credentials.emailPassword(email.value, password.value)).loggedIn
                        }
                        withContext(Dispatchers.Main) {
                            if (result) {
                                onSuccess()
                                delay(600)
                            } else {
                                onError(Exception("User is not logged in."))
                            }
                        }

                }else{
                    emptyField()
                }

            } catch (e: Exception) {
                withContext(Dispatchers.Main) {
                    onError(e)
                }
            }
        }
        }else{
            onInternetConnection()
        }

    }

